mapper.select:
select @rownum := @rownum + 1, bidx, bwriter, btitle, bcontent, bdate, bcnt, bvalue
from( select @rownum := 0, bidx, bwriter, btitle, bcontent, bdate, bcnt, bvalue, m.mid, 
	row_number() over(order by bidx desc) as rNum
    from board_tb b, member_tb m
    WHERE b.bwriter = m.mid
    
    ) pt
 where rNum between #{rowStart} and #{rowEnd}
 order by bidx desc;

mapper.update:
UPDATE board_tb
SET bcnt = bcnt + 1
WHERE bidx = #{bidx}

UPDATE board_tb 
SET	btitle = #{btitle},
	bcontent = #{bcontent}
WHERE bidx = #{bidx}

UPDATE board_tb
SET bvalue = 'n' 
WHERE bidx = #{bidx}
